sqlMap.select:
		
			select
 				date_add('2018-09-17', interval n.no week) as weeks,
				 date_format(
				    if(
				      weekday(
				        date_add('2018-09-17', interval n.no week)
				      ) = 4, 
				      date_add('2018-09-17', interval n.no week), 
				      date_add(
				        date_add('2018-09-17', interval n.no week), 
				        interval (
				          4 + weekday(
				            date_add('2018-09-17', interval n.no week)
				          )
				        ) DAY
				      )
				    ), 
				    '%m/%d'
				  ) AS formatFriday
				  from NUMBERS as n
				WHERE 
				  n.no < 6;
